declare const typeDefs = "#graphql\ntype Book {\nbookId: ID!\ntitle: String! \nauthors: [String!]!\ndescription: String!\nimage: String\nlink: String\n\n\n}\n\ntype Auth {\ntoken: ID!\nuser: User \n\n}\n\ntype User{\n_id: String!\nusername: String!\nemail: String!\nsavedBooks: [Book!]!\nbookCount: Int!\n}\n\ninput UserInput {\nusername: String!\nemail: String!\npassword: String!\n}\n\ninput SaveBookInput {\nauthors: [String!]\ndescription: String!\ntitle: String!\nbookId: ID!\nimage: String!\nlink: String!\n\n\n}\n\ntype Query {\nme: User\nuser(username: String!): User\n\n}\n\ntype Mutation {\naddUser(input: UserInput!): Auth\nlogin(email: String!, password: String!): Auth\nsaveBook(input: SaveBookInput!): User\nremoveBook(bookId: ID!): User\n\n\n\n}\n";
